    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_book_info)

        val pdfName = intent.getStringExtra("pdfName") ?: return
        val bookImageView = findViewById<ImageView>(R.id.bookImageView)
        val bookDescription = findViewById<TextView>(R.id.bookDescriptionTextView)
        val bookTitle = findViewById<TextView>(R.id.bookTitleTextView)
        val bookAuthor = findViewById<TextView>(R.id.bookAuthorTextView)
        val readButton = findViewById<Button>(R.id.readButton)

        val bookImageResId = resources.getIdentifier(pdfName, "drawable", packageName)
        bookImageView.setImageResource(bookImageResId)
        bookTitle.text = namae[pdfName]
        bookAuthor.text = authors[pdfName]
        bookDescription.text = descriptions[pdfName]

        readButton.setOnClickListener {
            val intent = Intent(this, PdfViewerActivity::class.java)
            intent.putExtra("pdfName", pdfName)
            startActivity(intent)
        }
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.LOLLIPOP) {
            window.navigationBarColor = resources.getColor(R.color.lightRed, theme)
        }
    }
